/*==============================================================*/
create table ATTR_SPEC
(
   ATTR_ID              NUMBER(16)           not null,
   BUSI_TYPE_ID         NUMBER(16),
   PAR_ATTR_ID          NUMBER(16),
   ATTR_NBR             VARCHAR2(30),
   ATTR_NAME            VARCHAR2(50),
   ATTR_DESC            VARCHAR2(2000),
   DEFAULT_VALUE        VARCHAR2(30),
   VALUE_FROM           VARCHAR2(30),
   VALUE_TO             VARCHAR2(250),
   IS_UNIQUE            NUMBER(2)
      constraint CKC_IS_UNIQUE_ATTR_SPE check (IS_UNIQUE is null or (IS_UNIQUE in (0,1))),
   IS_NULLABLE          NUMBER(2)
      constraint CKC_IS_NULLABLE_ATTR_SPE check (IS_NULLABLE is null or (IS_NULLABLE in (0,1))),
   ATTR_VALUE_DATA_TYPE VARCHAR2(3),
   IS_DANY_ATTR         NUMBER(2)
      constraint CKC_IS_DANY_ATTR_ATTR_SPE check (IS_DANY_ATTR is null or (IS_DANY_ATTR in (0,1))),
