   ATTR_FORMAT          VARCHAR2(2000),
   ATTR_LENGTH          NUMBER(9),
   EXT_ATTR_CD          VARCHAR2(30),
   STATUS_CD            VARCHAR2(10),
   CREATE_STAFF         NUMBER(16),
   UPDATE_STAFF         NUMBER(16),
   CREATE_DATE          DATE,
   STATUS_DATE          DATE,
   UPDATE_DATE          DATE,
   REMARK               VARCHAR2(2000),
   constraint PK_ATTR_SPEC primary key (ATTR_ID)
);

