create table BILL
(
   BILL_ID              NUMBER(18)           not null,
   PAYMENT_ID           NUMBER(18)           not null,
   PAYMENT_METHOD       NUMBER(9),
   BILLING_CYCLE_ID     NUMBER(9)            not null,
   ACCT_ID              NUMBER(18)           not null,
