comment on column PLCA_SECTION_ACTION_REL.STATUS_CD is
'状态';

comment on column PLCA_SECTION_ACTION_REL.CREATE_STAFF is
'创建人';

comment on column PLCA_SECTION_ACTION_REL.UPDATE_STAFF is
'修改人';

comment on column PLCA_SECTION_ACTION_REL.CREATE_DATE is
'创建时间';

comment on column PLCA_SECTION_ACTION_REL.UPDATE_DATE is
'修改时间';

comment on column PLCA_SECTION_ACTION_REL.STATUS_DATE is
'状态时间';

