      constraint CKC_TIME_LIMIT_TYPE_PLCA_TIM check (TIME_LIMIT_TYPE in (1,2,3,4)),
   STATR_DATE           DATE,
   END_DATE             VARCHAR2(30),
   START_TIME           DATE,
