/*==============================================================*/
create table TARIFF
(
   TARIFF_ID            NUMBER(9)            not null,
   TARIFF_TYPE          NUMBER(2)            not null
      constraint CKC_TARIFF_TYPE_TARIFF check (TARIFF_TYPE in (1,2,3)),
   PRICING_SECTION_ID   NUMBER(9)            not null,
   BALANCE_TYPE_ID      NUMBER(9)            not null,
   ACCU_TYPE_ID         NUMBER(9)            not null,
   ACCT_TYPE_GROUP_FLAG NUMBER(1)
      constraint CKC_ACCT_TYPE_GROUP_F_TARIFF check (ACCT_TYPE_GROUP_FLAG is null or (ACCT_TYPE_GROUP_FLAG in (0,1))),
   SERVICE_OFFER_ID     NUMBER(9),
   ACCT_ITEM_TYPE_ID    NUMBER(9)            not null,
   SUB_PRODUCT_ID       NUMBER(9),
   TARIFF_UNIT_ID       NUMBER(9)            not null,
   CALC_METHOD_ID       NUMBER(9)            not null,
   RATABLE_CYCLE_ID     NUMBER(9)            not null,
   RATE_UNIT            NUMBER(8)            not null,
   FIXED_RATE_VALUE_ID  NUMBER(9)            not null,
   SCALED_RATE_VALUE_ID NUMBER(9)            not null,
   CALC_PRIORITY        NUMBER(5)            not null,
   BELONG_CYCLE_DURATION NUMBER(9)            not null,
   NUMERATOR            NUMBER(9),
   CHARGE_PARTY_ID      NUMBER(1)
      constraint CKC_CHARGE_PARTY_ID_TARIFF check (CHARGE_PARTY_ID is null or (CHARGE_PARTY_ID in (1,2,3))),
   DENOMINATOR          NUMBER(9),
   ACCU_OWNER_TYPE      NUMBER(2)
      constraint CKC_ACCU_OWNER_TYPE_TARIFF check (ACCU_OWNER_TYPE is null or (ACCU_OWNER_TYPE in (1,2,3,4,5,6,7,8,9))),
   CALC_PRECISION_METHOD NUMBER(1)            not null
      constraint CKC_CALC_PRECISION_ME_TARIFF check (CALC_PRECISION_METHOD in (1,2,3)),
   CALC_RESULT_TYPE     NUMBER(2)
      constraint CKC_CALC_RESULT_TYPE_TARIFF check (CALC_RESULT_TYPE is null or (CALC_RESULT_TYPE in (1,2,3))),
   TARIFF_DESC          VARCHAR2(250),
   STATUS_CD            NUMBER(9)            not null,
   CREATE_STAFF         NUMBER(16)           not null,
   UPDATE_STAFF         NUMBER(16),
   CREATE_DATE          DATE,
   UPDATE_DATE          DATE,
   STATUS_DATE          DATE,
   constraint PK_TARIFF primary key (TARIFF_ID)
);

comment on table TARIFF is
'定义了对客户所使用的产品进行计费的基本费用信息，资费标准可分为一次性费用、周期性费用和使用费三种类型。';

