create table ZONE
(
   ZONE_ID              NUMBER(9)            not null,
   ZONE_NAME            VARCHAR2(32)         not null,
   REGION_ID            NUMBER(9)            not null,
   PRICING_REF_OBJECT_ID NUMBER(9)            not null,
   STATUS_CD            NUMBER(9)            not null,
   CREATE_STAFF         NUMBER(16)           not null,
   UPDATE_STAFF         NUMBER(16),
   CREATE_DATE          DATE                 not null,
   UPDATE_DATE          DATE                 not null,
   STATUS_DATE          DATE                 not null,
   constraint PK_ZONE primary key (ZONE_ID)
);

comment on table ZONE is
'区表用于表达一组互相管理的数据，用于把事件的属性值划分成易于管理的类别。如被叫地区、时区、传真分区、Qos分区等都可以做成区表进行管理。';

